SELECT DISTINCT A.id AS id, A.name AS name, A.version AS version, A.tips AS tips, A.publishtime AS publishtime, A.publish_range AS publishRange, B.name AS projectName, C.username AS operatorUsername, C.name AS operatorName FROM t_app A LEFT OUTER JOIN t_project B ON A.project_id=B.id LEFT OUTER JOIN t_manager C ON A.operator_id=C.id
A.project_id IN
#{i}
AND A.name = #{appName}
AND A.version = #{appVersion}
AND A.project_id = #{projectId}
AND A.publish_range = #{publishRange}
AND A.publishtime BETWEEN #{publishTimeStart} AND #{publishTimeEnd}
ORDER BY A.publishtime DESC
