mapper.select:
SELECT DISTINCT A.id AS id, A.imei AS imei, A.series AS series, A.jointime AS jointime, A.location AS location, A.pos_state AS posState, A.tips AS tips, B.name AS projectName FROM t_pos A LEFT OUTER JOIN t_project B ON A.project_id=B.id
A.project_id IN
#{i}
AND A.imei = #{imei}
AND A.location = #{location}
AND A.project_id = #{projectId}
ORDER BY A.jointime DESC

SELECT COUNT(DISTINCT location)
FROM t_pos

    location <> ''

        AND project_id = #{projectId}


        AND project_id IN

            #{i}

SELECT A.location AS location, B.name AS projectName, COUNT(*) AS posCount FROM t_pos A LEFT OUTER JOIN t_project B ON A.project_id=B.id
A.location <> '' AND A.project_id IN
#{i}
AND A.project_id = #{projectId}
AND A.location = #{location}
GROUP BY A.location, B.name

SELECT A.id AS id, A.report_time AS reportTime, A.report_location AS reportLocation, A.online_state AS onlineState, B.imei AS imei, B.series AS series FROM t_pos_monitor A LEFT OUTER JOIN t_pos B ON A.pos_id=B.id
B.location <> ''
AND B.imei = #{imei}
AND B.location = #{location}
AND B.project_id IN
#{i}
AND B.project_id = #{projectId}
AND A.online_state = #{onlineState}
ORDER BY A.report_time DESC
